import { useState, useEffect } from "react"; //this line added
import { Link, useParams, useNavigate } from "react-router-dom";
import axios from "axios";

const API = process.env.REACT_APP_API_URL;

function Transaction({ transaction, index }) {
  const [setTransaction] = useState(null); //this line added
  const { id } = useParams(); //added


  const navigate = useNavigate();


  useEffect(() => {
    axios
      .get(`${API}/transactions/${id}`)
      .then((response) => {
        setTransaction(response.data);
      })
      .catch(() => {
        navigate("/not-found");
      });
  }, [id, navigate]);



  // const handleDelete = () => {
  //   axios
  //     .delete(`${API}/transactions/${index}`)
  //     .then(() => navigate("/transactions"))
  //     .catch(console.error);
  // };

  const handleDelete = () => {
    axios
      .delete(`${API}/transactions/${id}`)
      .then(() => {
        setTransaction(null); // Remove the deleted transaction from the local state
        navigate(`/transactions`); // Navigate to the transaction list page
      })
      .catch((e) => console.error(e));
  };

  return (
    <tr className="Transaction">
      <td>{transaction.item_name}</td>
      <td>{transaction.amount}</td>
      <td>{transaction.date}</td>
      <td>{transaction.from}</td>
      <td>{transaction.category}</td>
      <td><Link to={`/transactions/${id}`}>Details</Link></td>
      <td>
        <button onClick={handleDelete}>Delete</button>
      </td>
    </tr>
  );
}

export default Transaction;
